class Solution(object):
    def getHint(self, secret, guess):
        """
        :type secret: str
        :type guess: str
        :rtype: str
        """
        bulls = 0
        cows = 0

        count_digits_secret = {}
        count_digits_guess = {}
        for i in xrange(len(guess)):
            if secret[i] == guess[i]:
                bulls += 1
            else:
                count_digits_secret[secret[i]] = count_digits_secret.get(secret[i],0) + 1
                count_digits_guess[guess[i]] = count_digits_guess.get(guess[i],0) + 1

        for digit in count_digits_secret:
            cows += min(count_digits_secret.get(digit,0), count_digits_guess.get(digit,0))

        return str(bulls) + "A" + str(cows) + "B"
        
